import React, {Component} from 'react'




class Dice extends Component{
    render(){
        return(
            <div>
                <h1> {this.props.rolls} </h1>
                <button onClick = {this.props.diceRoll}> Roll dice </button>
            </div>
        )
    }
}

export default Dice
