import React, {Component} from 'react';
import Dice from './component/dice.js';
import './App.css';

class App extends Component {
    constructor(){
        super()
        this.state = {
            rolls: []

        }
    }
    diceRoller = () => {
        let {rolls} = this.state
        rolls.push (Math.ceil(Math.random() * 6))
    }

    render(){
        return(
            <dice
            rolls={this.state.rolls}
            diceRoller = {this.diceRoller}
            />
        )
    }
}

export default App;
